//-------------------------------------------------------------------------------------------------------------
void CommonModelSystemBootTestHandler::registerMethods(IRtiMsgParser& msgParser, IRtiSettings& settings)
{
	// Create a directory to store the data in
	msgParser.addDir( new DirClass("/system/boot", "System boot timing", msgParser, settings) ) ;


	msgParser.add( static_cast<RtiDirEntry *>(new CommonModelSystemBootTestHandler("/system/boot/get",
			"",
			"ENUM(OK:ERROR) Status=OK,[STRING Info],[STRING Message],FLOAT appStart (s),FLOAT tifTime (s),FLOAT tifElapsed (s),FLOAT appElapsed (s),FLOAT bootTime (s)",
			"Get the boot timing values",
			msgParser, settings))
	);


	msgParser.add( static_cast<RtiDirEntry *>(new CommonModelSystemBootTestHandler("/system/boot/show",
			"",
			"ENUM(OK:ERROR) Status=OK,[STRING Info],[STRING Message]",
			"Show boot timing information",
			msgParser, settings))
	);





}




