void HelperAlarmsTestHandler::registerMethods(IRtiMsgParser& msgParser, IRtiSettings& settings)
{
	// Create a directory to store the data in
	msgParser.addDir( new DirClass("/alarms", "Alarms and faults", msgParser, settings) ) ;


	msgParser.add( static_cast<RtiDirEntry *>(new HelperAlarmsTestHandler("/alarms/portClose",
			"",
			"ENUM(OK:ERROR) Status=OK,[STRING Info],[STRING Message]",
			"Stops sending out alarm events",
			msgParser, settings))
	);


	msgParser.add( static_cast<RtiDirEntry *>(new HelperAlarmsTestHandler("/alarms/portOpen",
			"INT{1024..65535} port",
			"ENUM(OK:ERROR) Status=OK,[STRING Info],[STRING Message]",
			"Sends all alarm events to the specified port",
			msgParser, settings))
	);


	msgParser.add( static_cast<RtiDirEntry *>(new HelperAlarmsTestHandler("/alarms/showAll",
			"",
			"ENUM(OK:ERROR) Status=OK,[STRING Info],[STRING Message]",
			"Displays information on the state of all alarms",
			msgParser, settings))
	);


	msgParser.add( static_cast<RtiDirEntry *>(new HelperAlarmsTestHandler("/alarms/status",
			"",
			"ENUM(OK:ERROR) Status=OK,[STRING Info],[STRING Message]",
			"Shows the current alarm status",
			msgParser, settings))
	);





}




