{
	// Create a directory to store the data in
	msgParser.addDir( new DirClass("/arb/capture", "Arbitrary waveform capture control", msgParser, settings) ) ;


	msgParser.add( static_cast<RtiDirEntry *>(new HelperArbCaptureTestHandler("/arb/capture/files",
			"[STRING path]",
			"ENUM(OK:ERROR) Status=OK,[STRING Info],[STRING Message]",
			"Show all capture files",
			msgParser, settings))
	);


	msgParser.add( static_cast<RtiDirEntry *>(new HelperArbCaptureTestHandler("/arb/capture/start",
			"",
			"ENUM(OK:ERROR) Status=OK,[STRING Info],[STRING Message]",
			"Start capture on all enabled waveforms",
			msgParser, settings))
	);





}




