void HelperArbTestHandler::registerMethods(IRtiMsgParser& msgParser, IRtiSettings& settings)
{
	// Create a directory to store the data in
	msgParser.addDir( new DirClass("/arb", "Arbitrary waveform playback/capture interface", msgParser, settings) ) ;


	msgParser.add( static_cast<RtiDirEntry *>(new HelperArbTestHandler("/arb/set",
			"ENUM(LTE) airstd,ENUM(5:10:15:20) bw (MHz)",
			"ENUM(OK:ERROR) Status=OK,[STRING Info],[STRING Message]",
			"Set up arbitrary waveform",
			msgParser, settings))
	);


	msgParser.add( static_cast<RtiDirEntry *>(new HelperArbTestHandler("/arb/show",
			"",
			"ENUM(OK:ERROR) Status=OK,[STRING Info],[STRING Message]",
			"Display all settings",
			msgParser, settings))
	);





}




