//-------------------------------------------------------------------------------------------------------------
void HelperI2CTestHandler::registerMethods(IRtiMsgParser& msgParser, IRtiSettings& settings)
{
	// Create a directory to store the data in
	msgParser.addDir( new DirClass("/.i2c", "Common I2C control", msgParser, settings) ) ;


	msgParser.add( static_cast<RtiDirEntry *>(new HelperI2CTestHandler("/.i2c/lock",
			"",
			"ENUM(OK:ERROR) Status=OK,[STRING Info],[STRING Message]",
			"Lock the I2C bus",
			msgParser, settings))
	);


	msgParser.add( static_cast<RtiDirEntry *>(new HelperI2CTestHandler("/.i2c/read",
			"INT i2cAddress,INT firstDataByte,[INT secondDataByte]",
			"ENUM(OK:ERROR) Status=OK,[STRING Info],[STRING Message],HEXINT[] Hex",
			"Read from an I2C device",
			msgParser, settings))
	);


	msgParser.add( static_cast<RtiDirEntry *>(new HelperI2CTestHandler("/.i2c/unlock",
			"",
			"ENUM(OK:ERROR) Status=OK,[STRING Info],[STRING Message]",
			"Unlock the I2C bus",
			msgParser, settings))
	);


	msgParser.add( static_cast<RtiDirEntry *>(new HelperI2CTestHandler("/.i2c/write",
			"INT i2cAddress,INT firstDataByte,[INT secondDataByte]",
			"ENUM(OK:ERROR) Status=OK,[STRING Info],[STRING Message]",
			"Write to an I2C device",
			msgParser, settings))
	);





}




