void HelperImagesAppTestHandler::registerMethods(IRtiMsgParser& msgParser, IRtiSettings& settings)
{
	// Create a directory to store the data in
	msgParser.addDir( new DirClass("/image/app", "Application Image Management settings", msgParser, settings) ) ;


	msgParser.add( static_cast<RtiDirEntry *>(new HelperImagesAppTestHandler("/image/app/clean",
			"",
			"ENUM(OK:ERROR) Status=OK,[STRING Info],[STRING Message]",
			"Clean flash of old application images in order to free-up space",
			msgParser, settings))
	);


	msgParser.add( static_cast<RtiDirEntry *>(new HelperImagesAppTestHandler("/image/app/install",
			"STRING version,STRING build",
			"ENUM(OK:ERROR) Status=OK,[STRING Info],[STRING Message]",
			"Get and install application",
			msgParser, settings))
	);


	msgParser.add( static_cast<RtiDirEntry *>(new HelperImagesAppTestHandler("/image/app/installFile",
			"STRING filename",
			"ENUM(OK:ERROR) Status=OK,[STRING Info],[STRING Message]",
			"Get and install application from a file",
			msgParser, settings))
	);


	msgParser.add( static_cast<RtiDirEntry *>(new HelperImagesAppTestHandler("/image/app/show",
			"",
			"ENUM(OK:ERROR) Status=OK,[STRING Info],[STRING Message]",
			"Show application image information",
			msgParser, settings))
	);





}




