//-------------------------------------------------------------------------------------------------------------
void HelperSystemTcpTestHandler::registerMethods(IRtiMsgParser& msgParser, IRtiSettings& settings)
{
	// Create a directory to store the data in
	msgParser.addDir( new DirClass("/system/tcp", "TCP ports", msgParser, settings) ) ;


	msgParser.add( static_cast<RtiDirEntry *>(new HelperSystemTcpTestHandler("/system/tcp/close",
			"INT port",
			"ENUM(OK:ERROR) Status=OK,[STRING Info],[STRING Message]",
			"Close an open tcp port",
			msgParser, settings))
	);


	msgParser.add( static_cast<RtiDirEntry *>(new HelperSystemTcpTestHandler("/system/tcp/open",
			"INT{1024..65535} port,[INT connections=1],[ENUM(IN:OUT:INOUT) type=OUT]",
			"ENUM(OK:ERROR) Status=OK,[STRING Info],[STRING Message]",
			"Create a tcp port handler",
			msgParser, settings))
	);


	msgParser.add( static_cast<RtiDirEntry *>(new HelperSystemTcpTestHandler("/system/tcp/show",
			"",
			"ENUM(OK:ERROR) Status=OK,[STRING Info],[STRING Message]",
			"Show all created tcp ports",
			msgParser, settings))
	);





}




